//=================Serie_Revision
//---------------------------ex1:
import React,{useState} from 'react'

export default function App() {
  const trains=[
    {id:'t1',nom:'Buraq',villes:[]},
    {id:'t2',nom:'Gazel', villes:[]},
    {id:'t3',nom:'Eagle-Wins', villes:[]},
    {id:'t4',nom:'Fennec', villes:[]},
    ]
  
    const [id,setId]=useState()
    const [train,setTrain]=useState()
    const [ville,setVille]=useState('')

    function rechercher(){
      setTrain(trains.find((t)=>t.id==id))
    }

    function ajouterVille(){
      if(train){
        train.villes.push(ville);
    }
    }

  return (
    <div>
      <div>Gestion des trains</div>
            <div>Id: <input type="text" onChange={(e)=>setId(e.target.value)}/>
            <button onClick={rechercher}>rechercher</button>
            </div>
            <div>Nom du train: {train?train.nom:''} </div>
            <div>Id: <input type="text" onChange={(e)=>setVille(e.target.value)}/>
            <div>Volle de dÃ©part:{train && train.villes.length>0?train.villes[0]:''} </div>
            <div>Ville terminus: {train && train.villes.length>0?train.villes[train.villes.length-1]:''} </div>
            <button onClick={ajouterVille}>Ajouter ville</button>
            </div>
            <table>
                    <tr>
                        <td>Nom de la ville  </td>
                        <td>Ordre de passage</td>
                        <td></td>
                    </tr>
                    {
                        train.villes.map((v,i)=> {return(
                          <tr>
                             <td>{v}  </td>
                             <td>{(i+1)}</td>
                             <td><button onClick={()=>supprimer(i)} >Supprimer</button></td>
                          </tr>

                        )})
                    }
                </table>
            </div>
  
  )
}
