import { configureStore,createSlice } from "@reduxjs/toolkit";

const initState = { color:"white"};
const bgColors=createSlice({
    name:'couleurs',
    initialState:initState,
    reducers:{
        dark:(state)=>{
            state.color="black";
        },
        light:(state)=>{
            state.color="white";
        },
    }
})

export const{dark,light}=bgColors.actions

export const store=configureStore({
    reducer:{
        couleurs:bgColors.reducer,
    }
})
