import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router";
import { increment, descrement } from "./store";
export default function Counter() {
  const navigt = useNavigate();
  const cntr = useSelector((data) => data.counter.val);
  const dispatch = useDispatch();
  return (
    <div className="d-flex justify-content-center">
      <div className="container card shadow-lg m-2 p-2 col-8">
        <div className="text-center mb-2 fs-2 text-info">{cntr}</div>
        <div className="d-flex justify-content-center gap-3">
          <button
            className="col-3 btn btn-success"
            onClick={() => dispatch(increment(1))}>increment</button>

          <button
            className="col-3 btn btn-warning"
            onClick={() => dispatch(descrement(1))}
          >
            descrement
          </button>
        </div>

        <div className='text-center mt-3 '>
          <button className='col-10 btn btn-info text-light' onClick={() => navigt("/")}>logout</button>
        </div>
      </div>
    </div>
  );
}
