import './App.css';
import React, { Component } from 'react'

 class App extends Component {
  state={
    nom:"moha",
    age:30
  }
  afch=()=>{
    return <>
    {this.state.nom} {this.state.age}
    </>
  }
  chnge=(aa,bb)=>{
    return this.setState({
      nom:aa,
      age:bb
    })
  }
  render() {
    const stl={padding:''}
    return (
      <div>
        <h2>{this.afch()}</h2>
        <button onClick={()=>this.chnge('mohamed',300)}>chang</button>
      </div>
    )
  }
}
export default App











