import { useState } from 'react';
export default function App() {
    const [slct,setSlct]=useState()
    let elments=["orange","red","blue",'green',"yellow"];
    document.body.style=`background-color:${slct} ;`
   return(
    <div>
     <select onChange={(e)=>setSlct(e.target.value)}>
     {elments.map(function(option, index) {
  return <option key={index+1}
  value={option}>{option}</option>})
  }
     </select>
    </div>
   )
  }
