import {useState,useEffect} from 'react'

const courses=[
    {id: "MarcheF",nom:"Marche femmes",categorie:"F", distance:10000
    },
{id: "MarcheH",nom:"Marche hommes",categorie:"H", distance:10000
    },
{id: "MarathonF",nom:"Marathon fommes",categorie:"F", distance:42000
    },
{id: "MarathonH",nom:"Marathon hommes",categorie:"H", distance:42000
    }
]

const participants=[]

export default function Course(){
    const[index,setIndex]=useState(0)
    const[c,setC]=useState(courses[0])
    const[affCourse,setAffCourse]=useState(1)
    const [participant,setParticipant]=useState()
    useEffect(() => {
        //Runs on the first render
        //And any time any dependency value changes
        setC(courses[index])
      }, [index]);


    
    

    function Nouveau(){
        const [idP,setIdP]=useState()
        const [name,setName]=useState('')
        useEffect(() => {
            //Runs on the first render
            //And any time any dependency value changes
            maxId()
          }, [idP]);

        function maxId(){

            let m=0
            for(let i=0;i<participants.length;i++){
                if(m<participants[i].id)m=participants[i].id

            }
            setIdP(m+1)
        }

        function ok(){
            const newP={id:idP,nom:name,idCourse:c.id}
 
            if(c && name){
                participants.push(newP)

            }
            
            setAffCourse(1)
        }
        function cancel(){
            setAffCourse(1)
        }



        return(

            <div style={{margin:"20px"}}>
                <h3>Nouveau participant</h3>
                <h3>Participant ID......:{idP}</h3>
                <h3>Participant Name....: 
                <input type="text" style={{margin:"5px", fontSize: "16px"}}
                onChange={(e)=>setName(e.target.value)}/>
                </h3>
                <h3>Course ID......:{c.id}</h3>
                <button style={{marginLeft:"10px", fontSize: "16px"}} onClick={()=> ok()}>Ok</button>
                <button style={{marginLeft:"10px", fontSize: "16px"}} onClick={()=> cancel()}>Cancel</button>


            </div>
        )

    }

    function EditParticipant(props){
        
        function ok(){
            
            
            setAffCourse(1)
        }
        function cancel(){
            setAffCourse(1)
        }




        return(

            <div style={{margin:"20px"}}>
                <h3>Edit participant</h3>
                <h3>Participant ID......:{props.part.id}</h3>
                <h3>Participant Name....: 
                <input type="text" defaultValue={props.part.nom} style={{margin:"5px", fontSize: "16px"}}
                onChange={(e)=> props.part.nom=e.target.value}/>
                </h3>
                <h3>Course ID......:{props.part.idCourse}</h3>
                <button style={{marginLeft:"10px", fontSize: "16px"}} onClick={()=> ok()}>Ok</button>
                <button style={{marginLeft:"10px", fontSize: "16px"}} onClick={()=> cancel()}>Cancel</button>


            </div>
        )
    }

    function Crse(){
        const[suppression,setSuppression]=useState(0)

        

        
        function first(){
            setIndex(0)
            //setC(courses[0])
        }
    
        function next(){
            if(index<courses.length-1){
                setIndex(index +1)
                //setC(courses[index])
        }
        }
    
        function previous(){
            if(index>0){
                setIndex(index -1)
                //setC(courses[index])
        }
        }
    
        function last(){
            if(courses.length>0){
                setIndex(courses.length-1)
                //setC(courses[index])
                
        }
        }
    
        function nouveauP(){
            setAffCourse(2)
        }
    

        function supprimer(i){
            let j=0
            for(let k=0;k<participants.length;k++){
                if(participants[k].id==i){
                    j=k
                    break
                }
            }
            participants.splice(j,1)
            suppression?setSuppression(0):setSuppression(1)
            
        }

        function modifier(p){
            setParticipant(p)
            setAffCourse(3)

        }

        function LigneParticipant(props){
            return(
                <tr key={props.k}>
                    <td>{props.part.id}</td>
                    <td>{props.part.nom}</td>
                    <td>{props.part.idCourse}</td>
                    <td><button onClick={()=>supprimer(props.part.id)}>Supprimer</button></td>
                    <td><button onClick={()=>modifier(props.part)}>Modifier</button></td>
                </tr>
            )
        }
        return(
            <div style={{margin:"20px"}}>
                <h3>Compétition d'athlétisme</h3>
                <button style={{ fontSize: "16px"} } onClick={()=> first()}>First</button>
                <button style={{marginLeft:"10px", fontSize: "16px"}} onClick={()=> previous()}>Previous</button>
                <button style={{marginLeft:"10px", fontSize: "16px"}} onClick={()=> next()}>Next</button>
                <button style={{marginLeft:"10px", fontSize: "16px"}} onClick={()=> last()}>Last</button>
    
                <h3>Course ID........:{c.id}</h3>
                <h3>Course Name..:{c.nom}</h3>
                <h3>Category.........: 
                <input type="radio" id="f" name="cat"  checked={c.categorie=="F"}/>
                <label for="f">Femmes</label>
                <input type="radio" id="h" name="cat" checked={c.categorie=="H"}/>
                <label for="h">Hommes</label></h3>
                <h3>Distance..........{c.distance}:</h3>
                <button style={{fontSize: "16px"}} onClick={()=> nouveauP()}>Nouveau participantLast</button>

                <h2>Participants</h2>
                <table>
                    <tr>
                        <td>Id participant</td>
                        <td>Nom</td>
                        <td>Id course</td>
                        <td> </td>
                        <td> </td>

                    </tr>
                

                {
                    participants.filter((x)=>x.idCourse==c.id).map((y,i)=><LigneParticipant k={i} part={y} />)

                }
               </table>
                
    
            </div>
    
        )


    }

    return(
        <>
        {c && affCourse==1?<Crse/>:c && affCourse==2?<Nouveau/>:<EditParticipant part={participant}/>}
        </>
    )
}












