import React from 'react'
import { useState } from 'react'
import './App.css'
export default function App() {
  const [nb1,setnb1]=useState(0);
  const [nb2,setnb2]=useState(0);
  const [rslt,setrslt]=useState(0);
  const afficherRslt=()=>{
    setrslt(nb1+nb2);
  }
  return (
    <div className='App'>
      <div>
         <label>entrez nomber 1 </label> <input  type="number" onChange={(e)=>{setnb1(e.target.value)}}/>
      </div>
      <div>
        <label>entrez nomber 2 </label><input  type="number"onChange={(e)=>setnb2(e.target.value)} />
      </div>
      <div><button onClick={()=>afficherRslt}>addition</button></div>
      <div>
        <label>resultats</label>  <input  type="text" value={rslt} readOnly   />
      </div>
    </div>
  )
}
