// import User from './components/User';
import React, { Component } from 'react';
class App extends Component {
  constructor(props){
    super(props);
    this.state={
      name:'Mohamed',
      age:'20'
    }
  }
  seyHi=()=>(
    <h2>
      {this.state.name} {this.state.age}
    </h2>
  )
    
    chang=(n,a)=>{
      this.setState({
      name:n,
      age:a
      })
    }
    render() { 
 
        return (
          <div>
            <h3>{this.seyHi()}</h3>
         <button style={{backgroundColor:'beige'}}  onClick={()=>this.chang('walid','12')}>chnage</button>
         {/* <div>{this.state.name}</div>
         <div>{this.state.age}</div> */}
           </div>
          );
    }
}
export default App;
