import React, { createContext, useState } from 'react'
export const Them=createContext();
function ThmClr(props) {
  const[theme,setTheme]=useState('dark');
  function toggleTm(){
    setTheme(theme==='dark'?'light':'dark');
  }
  return (
    <Them.Provider value={{theme,toggleTm}}>
        <div></div>
    </Them.Provider>
  
  )
}
