import React from 'react'
import { useState } from 'react'

export default function App() {
  const velos=[
    {ref:"v1",marque:'fennce',automotie:0,kilometrage:0,parcours:[]},
    {ref:"v2",marque:'dacia',automotie:0,kilometrage:0,parcours:[]},
    {ref:"v3",marque:'wins',automotie:0,kilometrage:0,parcours:[]},
    {ref:"v4",marque:'fennce',automotie:0,kilometrage:0,parcours:[]},
  ]
  const [velo,setVelo]=useState()
  const [ref,setref]=useState()
  const[charger,setcharger]=useState()
  const[distance,setdestance]=useState()

  function rechercher(){
    setVelo(velos.find((e)=>e.ref==ref))
  }
  function cherchvelo(){
    velo.automotie+=charger
    setVelo((prev)=>({
      ...prev,automotie:velo.automotie
        }))
  }
  return (
    <div>
    <div> ref <input type='text' onChange={(e)=>setref(e.target.value)}/> </div>
    <div> Marque <input type='text' value={velo?velo.marque:""}/> 
    <button onClick={rechercher} >rechercher</button> </div>
    <div> auto <input type='text' value={velo?velo.automotie:''}/> </div>
    <div> kilo <input type='text' value={velo?velo.kilometrage:''}/> </div>
    <div> nbr munit <input type='text' onChange={(e)=>setcharger(+e.target.value)}/>
      <button onClick={cherchvelo}>charger</button> </div>
    <div> distance parciur <input type='text' onChange={(e)=>setdestance(+e.target.value)}/> 
     <button>Ajouter parcours</button> </div>
     <table>
      <tr>
        <th>id</th>
        <th>destance</th>
      </tr>
      {velo.parcours.map()}
     </table>
    </div>
  )
}
