class veloElectrique{
  constructor(ref=0,prix=0,autonomie=0,kilo=0){
    this.ref=ref;
    this.prix=prix;
    this.autonomie=autonomie;
    this.kilo=kilo;
  }
  charger(nbM){
    if(nbM/60*60<=40){
      this.autonomie=nbM/60
    }else{
      this.autonomie=40
    }
  }
  rouler(distac){
    this.kilo+=distac;
    this.autonomie-=distac;
  }
  infos(){
    return ` le autonomie est: ${this.autonomie} le kilo est: ${this.kilo}`;
  }
}
