  }
  return (
    <div>
      <input type='text' onChange={(e)=>setText(e.target.value)}/>
      <button onClick={handlClick}>Add</button>
      {rslt?rslt.map((e)=>{
        return <ul>
          <li key={e.id}>{e.nom}</li>
        </ul>
      }):'vide'}
    </div>
  )
}
