const initialreducer=(state={color:"black"},action)=>{
    switch(action.type){
        case 'DARK':
            return {clr:state.color="black"}
            // return {...state, val : state.val + 1};
        case 'LIGHT':
            return {clr:state.color="red"}
        default: 
            return state;

    }
}
export default initialreducer





