import React from 'react'
import { useState } from 'react'
export default function App(){
  const[items,setItems]=useState([])
  const[afch,setAfch]=useState()
  function affItems(){
  const item={
    id:Math.floor(Math.random()*1000),
    itm:items
  }
  setAfch(olditm=>[...olditm,item])
  }
  return(
    <div className='text-center'>
    <h2>Todo List App</h2>
    <input type='text' placeholder='add an item...' onChange={(e)=>setItems(e.target.value)}/>
    <button onClick={()=>affItems()}>add</button>
    <div>
      <ul>
      {afch?afch.map((e)=>{
        return <li>{e.itm}</li>
      }):""}
      </ul>
    </div>
    </div>
  )
}
