  const[afficher,setAfficher]=useState([])
  function afch(){
    const item={
      id:Math.floor(Math.random()*1000),
      val:list
    }
    setAfficher(oldlist=>[...oldlist,item])
  }
  return (
    <div>
      <input type='text' onChange={(e)=>setList(e.target.value)}/> <button>add</button>
    </div>
  )
}
