import React, { useState } from 'react'
import './App.css'
export default function App() {
    const courses=[ 
                {id: "MarcheF",nom:"Marche femmes",categorie:"F", distance:10000
                }, 
               {id: "MarcheH",nom:"Marche hommes",categorie:"H", distance:10000
                }, 
               {id: "MarathonF",nom:"Marathon fommes",categorie:"F", distance:42000
                }, 
               {id: "MarathonH",nom:"Marathon hommes",categorie:"H", distance:42000
                } 
               ] ;
        const[list,setList]=useState('')
  return (
    <div>
      <input type='text' onChange={(e)=>setList(e.target.value)}/>
      <table>
        <tr>
            <th>Id</th>
            <th>Nom</th>
            <th>Categorie</th>
            <th>Distance</th>
        </tr>
        {courses.filter(etm=>
            list.toLocaleLowerCase()===""?etm:etm.id.toLocaleLowerCase().includes(list)).map(e=>
            <tr>
                <td>{e.id}</td>
                <td>{e.nom}</td>
                <td>{e.categorie}</td>
                <td>{e.distance}</td>
            </tr>)}
      </table>
    </div>
  )
}




















