import React,{Component} from 'react';
import './App.css';
class App extends Component{
  state={
    name:""
  }
  fncChing=(evnt)=>{
    this.setState({
      name:evnt.target.value
    })
  }
  rsltfinl=(evnt)=>{
    evnt.preventDefault();
    <h3>ajouter dans console</h3>
    console.log(this.state.name)
  }
  render(){
    return(
      <div className='App'>
      <form onSubmit={this.rsltfinl}>
        <select onChange={this.fncChing}>
          <option value='pizza'>pizza</option>
          <option value='salade'>salade</option>
          <option value='Cafe'>Cafe</option>
          <option value='Jus'>Jus</option>
        </select>
        <button  >valid</button>
      </form>
      <div className='tab'>
        <table>
          <thead>
            <th>Results Final</th>
          </thead>
          <tbody>
            <td><div className='rslt'>{this.state.name}</div></td>
          </tbody>
        </table>
      </div>
      </div>
    )
  }
}export default App;
