import React,{useState} from 'react'
import axios from 'axios'

export default function Users({user,activeId,handleChangeActiveId}) {
    const [posts,setPosts]=useState([])
    function HandelPosts(){
        const getData = async () => {
        const posts = await axios.get("https://jsonplaceholder.typicode.com/posts")
        setPosts(posts.data.filter((post)=>post.userId===user.id));
    };
    getData();
    handleChangeActiveId(user.id)

}
return(<div className="child" key={user.id}>
<h3>nom: {user.name} {user.username}</h3>
<p>email:{user.email}</p>
<p> ville:{user.address.city}
rue:{user.address.street} </p>
<button onClick={HandelPosts}>details posts</button>
{activeId==user.id &&(
<div>
<h5 style={{color:"green"}}>nombre des posts: {posts.length}</h5>
{posts.map(post=>{
return(<div className="post" key={post.id}>
<h5>{post.title}</h5>
<p>{post.body}</p>
</div>)
})}
</div>
)}
</div>)
}
