    <div>
        <div>{inpt}</div>
       <div>
        <span>Commetition d'athletisme </span>
       </div>
       <div>
        <span>Course Name : </span> <input type="text"/>
        <button onClick={()=>virif()}>gouche</button>
         <button>droite</button>
       </div>
       <div><span>Cathegorie :</span>
            <input type="radio" name='rad'/> Homme
            <input type="radio" name='rad'/> Fammme
        </div>
        <div>
            <span>Participants</span>
        </div>
        <table>
            <tr>
                <th> ID participantsnt </th>
                <th> nom </th>
                <th> ID course </th>
                <th> Action </th>
            </tr>
            {
            inpt==="MarcheF"?
            <tr>

            </tr>:alert('nooo')
            
            }
        </table>
    </div>
  )
}
