import React, { useContext } from 'react';
import{Theme} from'../App'
function Pa1() {
  var clr=useContext(Theme);
  let btn=document.querySelector('button');
  btn.addEventListener('click',()=>{
     let styl={}
    if(clr == "dark"){
      styl={
        background :'blue',
        color:'white',
        textAlign:'center'
    }
    }else{
      styl={
          background :'red',
          color:'white',
          textAlign: 'center'
      }
    }
  })
 
  return (
    <div style={styl}>
      <h1>salam</h1>
      <button id='btn'>click</button>
    </div>
  )
}
export default Pa1
