import React, { useState } from 'react';
export default function App() {
  const courses=[ 
    {id: "MarcheF",nom:"Marche femmes",categorie:"F", distance:10000
    }, 
   {id: "MarcheH",nom:"Marche hommes",categorie:"H", distance:10000
    }, 
   {id: "MarathonF",nom:"Marathon fommes",categorie:"F", distance:42000
    }, 
   {id: "MarathonH",nom:"Marathon hommes",categorie:"H", distance:42000
    } 
   ] ;

   const participants=[ 
   {id: "1", nom:"Med", idCourse: "MarcheH"}, 
   {id: "2",nom:"Rajae", idCourse: "MarcheF"} 
   ]


  // const participants=[]

   const[index,setIndex]=useState(0)
   const[aficheParticip,setaficheParti]=useState(participants)

   function DeletParticip(id){
   setaficheParti(aficheParticip.filter(it=>it.id!==id))
   }

   
   const[afchFormlure,setAfchFormlure]=useState('none')
   const[nvparti,setNvparti]=useState()

   let toggleAffich={display:`${afchFormlure}`}

   function afchParticipant(){
    setNvparti(courses.find((par)=>par.id===courses[index].id))
   }

   function afchNvl(){
    const nom=document.getElementById('NblNom').value
    const idCourse=courses[index].id;

    // const iid=Math.max(...aficheParticip.map(m=>m.id))
    const iid=aficheParticip.length
    const newParti={
      id:iid+1,
      nom,
      idCourse
    }
    if(nom===''){
      alert('saisir le champ nom svp')
    }else{
      setaficheParti((l)=>l.concat(newParti))
    }
    
   }
  

  return (
    <div className='d-flex flex-column justify-content-center align-items-center'>
    <div >
    <div >
    <div>
     <span >  Commetition d'athletisme</span>
    </div>
    <div >
      <b>Course ID </b>
      <input type="text" id='inpt' readOnly value={courses[index].id}/>
      <button onClick={()=>setIndex(0)}>{'|<'}</button>
      <button onClick={()=>setIndex(index>=1?index-1:index)} >{'<<'}</button>
      <button onClick={()=>setIndex(index<courses.length-1?index+1:index)}>{'>>'}</button>
      <button onClick={()=>setIndex(courses.length-1)}>{'>|'}</button>
    </div>
    <div >
      <b>Course Name </b>
      <input type='text' readOnly value={courses[index].nom}/>
    </div>
    <div className='mt-3 '>
      <b>Distance </b>
      <input type='text'  readOnly value={courses[index].distance}/>
      <a href='#pati' onClick={afchParticipant}> Nouveau participant</a>
    </div>
    <div>
         <b>Cathegorie </b>
         <input type="radio"checked={courses[index].categorie==='H'?true:false} name='rad'/> Homme
         <input type="radio"checked={courses[index].categorie==='F'?true:false} name='rad'/> Fammme
     </div>
     <div className='mt-3'>
         <span className='text-primary fs-4 ' >Participants</span>
     </div>
     <table>
         <tr>
             <th> ID participantsnt </th>
             <th> nom </th>
             <th> ID course </th>
             <th> Action </th>
         </tr>
      {aficheParticip.filter((etm)=>{
       return etm.idCourse===courses[index].id
      }).map((ex,id)=>{
       return <tr key={id} >
       <td>{ex.id}</td>
       <td>{ex.nom}</td>
       <td>{ex.idCourse}</td>
       <td>
      <button  onClick={()=>DeletParticip(ex.id)}>sup</button></td>
       </tr> 
      }) 
      }
     </table>
 </div>
 </div>

                            {/* formlure */}
 <div >
      <h5 >Nouveau participant </h5>
      <div >
         <div >
      <b>ID participant</b>
      <b>Nom</b>
      <b>ID course</b>
      </div>

      <div>
        <input type='text' readOnly value={aficheParticip.length+1}/>
        <input type='text' />
        <input type='text' readOnly value={nvparti?nvparti.id:''}/>
      </div>
      </div>

      <div>
        <button >Cancel</button>
        <button >ok</button>
      </div> 
 </div>
 </div>
  )
}
