import { DECREMENT, INCREMENT } from "../actions/type";
const ConunterReducer=(state = {val:0}, action)=>{
    switch(action.type){
        case INCREMENT :
            return {...state, val : state.val + 1};
        case DECREMENT :
            return {...state, val : state.val - 1};
        default :
            return state;
    }
}
export default ConunterReducer;
