import React, { useContext } from 'react'
import {Them} from './ThmClr';
function Accuil() {
const thm=useContext(Them);
let style={}
if(thm.theme==="dark"){
  style={
    background :'dark',
    color:'white',
    textAlign:'center'
}
}else{
  style={
      background :'white',
      color:'dar',
      textAlign: 'center'
  }
}
  return (
    <div style={style}>
      page Accuil
    </div>
  )
}

export default Accuil
