import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { AjouterAction,SuprmierAction } from './Redux/InitialAction'
export default function Affihcer() {
  const [valu,setValu]=useState()
  const dispatch= useDispatch()
    const content=useSelector((data)=>data.val)

    const handlClick=()=>{
      dispatch(AjouterAction({
        id:Math.floor(Math.random()*1000),
        elm:valu
      }))
      setValu("")
    }

    function handlDelete(iid){
      dispatch(SuprmierAction(iid))
    }

  return (
    <div className='container card shadow-lg m-2 p-2'>
        <input type='text' className='10' value={valu} onChange={(e)=>setValu(e.target.value)}/>
        <button className='2' onClick={handlClick}>Valider</button>
        <div>
           
                {content?content.map((e)=>{
                    return <ul style={{"listStyleType":"none"}}>
                        <li key={e.id}><span>{e.elm}</span>
                         <button onClick={()=>handlDelete(e.id)}>DELETE</button>
                        </li>
                    </ul>
                }):""}
            
        </div>
    </div>
  )
}
