import React, { useState } from 'react';
import './App.css';
function App() {
  let[contr,setContr]=useState('choisir u');
  let zid=()=>{
    setContr(contr+1)
  }
  return (
    <div className='App'>
      <button onClick={zid}>click</button>
      <div>{contr}</div>
    </div>
   
  )
}
export default App
