import React from 'react';
import { Link } from 'react-router-dom';

export default function Produits({productsAp}) {
  return (
 
    <div className='d-flex justify-content-center'>
      <div className='card shadow-sm col-11 p-2'>
        <h3 className='text-primary'>Ordinateurs portable </h3>
        <div className="row">
          {productsAp.map((elm,index)=>{
            return <div key={index} className="card shadow-sm col-3">
            <img className="bd-placeholder-img card-img-top"src={elm.thumbnail} alt="" />
            <div className="card-body">
            <p className="card-title">{elm.title}</p>
            <p className="card-text">{elm.price}</p>
            <div className="d-flex justify-content-between align-items-center">
              <div className="btn-group">
                <Link to={`/singleProduit/:${elm.id}`}>
                <button className="btn btn-sm btn-outline-secondary">Ajouter au panier</button>
                </Link>
             
              </div>
            </div>
            </div>
          </div>
          })}
        
        </div>
      </div>
    </div>

  )
}
