import React, { useState } from 'react'

export default function App() {
  const velos = [
    {ref:'v1', marque:'ford', autonomie:0, kilometrage:0, parcours:[] },
    {ref:'v2', marque:'dacia', autonomie:0, kilometrage:0, parcours:[] },
    {ref:'v3', marque:'kia', autonomie:0, kilometrage:0, parcours:[] },
    {ref:'v4', marque:'feat', autonomie:0, kilometrage:0, parcours:[] }
]
const [veloRef, setVeloRef] = useState()
const [distanceParc, setDistanceParc] = useState()
const [tab, setTab] = useState([])

function rechercher(){
  setTab(velos.find((v) => v.ref === veloRef));
}

function ajouter_parcours(){
  tab.parcours.push({parc:distanceParc})
  setTab((prev)=>({
    ...prev,parcours:tab.parcours
  }))
  setVeloRef('')
}

  return (
    <div>
        <h4 className='text-primary'>les formations</h4>
        <input type='text' placeholder='v1' value={veloRef} onChange={(e)=>setVeloRef(e.target.value)}/>
       <button  onClick={rechercher}>rechercher</button>

       <div>la marque est: {tab.marque?tab.marque:""}</div>

       <input type='text' placeholder='ajouter parcours'value={distanceParc} onChange={(e)=>setDistanceParc(e.target.value)}/>
       <button onClick={ajouter_parcours}>ajouter_parcours</button>
       <br/>
       <table>
                <tr>
                    <th>id parcours</th>
                    <th>distance</th>
                </tr>
                {
                    (tab.parcours)?tab.parcours.map((velo,index) => {
                        return <tr key={index+1}>
                            <td> {index+1} </td>
                            <td> {velo.parc} </td>
                        </tr> 
                    }):''
                }
            </table>
    </div>
  )
}
