import React, { useState } from 'react';

export default function App() {
  const [nbr1,setnbr1]=useState()
  const [nbr2,setnbr2]=useState()
  const [mulit,setmulit]=useState()
  const [rslt,setReslt]=useState()

  function calc(){
   if(mulit==="choisir"){
    setReslt('choisir svp')
   }
  else if(mulit==='+'){
    setReslt(Number(nbr1)+Number(nbr2))
   }
   else if(mulit==='x'){
    setReslt(Number(nbr1)*Number(nbr2))
   }
   else if(mulit==='/'){
    setReslt(Number(nbr1)/Number(nbr2))
   }
   else if(mulit==='-'){
    setReslt(Number(nbr1)-Number(nbr2))
   }
  }

  return (
    <div>
     <input type='number' placeholder='nomber 1' onChange={(e)=>setnbr1(e.target.value)}/>
     <input type='text' placeholder='nomber 2'onChange={(e)=>setnbr2(e.target.value)}/>
     <select onChange={(ex)=>setmulit(ex.target.value)}>
      <option value='choisir'>choisir</option>
      <option value='+'>+</option>
      <option value='x'>x</option>
      <option value='/'>/</option>
      <option value='-'>-</option>
     </select>
     <button onClick={calc}>calcule</button>
     <div>resultat :<b style={{color:'red'}}>{rslt}</b> </div>
    </div>
  )
}







