import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import{dark,light} from './store'
export default function Theme() {
  
    const bgclr=useSelector((data)=>data.colors.color);
    document.body.style=`background-color:${bgclr}`
    const dispatch=useDispatch()
  return (
    <div>
        <button onClick={()=>dispatch(dark())}>add</button>
        {bgclr}
        <button onClick={()=>dispatch(light(''))}>light</button>
    </div>
  )
}
