import React from 'react'
import { useSelector,useDispatch } from 'react-redux';
import { useNavigate } from 'react-router'

import { increment,decrement,reset } from './Redux/actions/ReduxAction';
export default function Counter() {
  const navigate=useNavigate()
  const vals=useSelector((state)=>state.val)
  const dispatch = useDispatch(); 
  return (
    <div className='d-flex justify-content-center'>
    <div className='container card shadow-lg m-2 p-2 col-8'>
         <div className='text-center mb-2 fs-2'>{vals}</div>
      <div className='d-flex justify-content-center gap-3'>
      <button className='col-3 btn btn-success' onClick={() =>dispatch(increment())}>Increase</button>
       <button className='col-3 btn btn-danger' onClick={() =>dispatch(reset())}>Reset</button>
       <button className='col-3 ' onClick={() =>dispatch(decrement())}>Decrease</button>
      </div>
     

       <div><button onClick={()=>navigate('/')}>logout</button></div>
    </div>
    </div>
  )
}









