axios.get('https://jsonplaceholder.typicode.com/users')
 .then((res)=>{ console.log(res.data);setUtilisateurs(res.data)})
},[])
const afficher=()=>{
    setUtilisateurs(utilisateurs.map(user=>user.address.city))
}
return(
<div>
<label>votre ville :</label>
</div>

)
}




