import React, { useState } from 'react';

export default function App() {
  const [nbr1,setnbr1]=useState()
  const [nbr2,setnbr2]=useState()
  const [mulit,setmulit]=useState()

  return (
    <div>
     <input type='text' placeholder='nomber 1' onChange={(e)=>setnbr1(e.target.value)}/>
     <input type='text' placeholder='nomber 2'onChange={(e)=>setnbr2(e.target.value)}/>
     <select onChange={(ex)=>setmulit()}>
      <option value='choisir' >choisir</option>
      <option value='+'>+</option>
      <option value='x'>x</option>
      <option value='/'>/</option>
      <option value='-'>-</option>
     </select>
     <input type='text' readOnly/>
    </div>
  )
}
