import './App.css';
import React, { Component } from 'react';
 class App extends Component {
  state={
    nom:'Mohammed',
    age:20,
    matier:'devlpmet'
  }
  nvl=()=>{
    this.setState({
      nom:'walid',
      age:15,
      
    })
  }
  render() {
    return (
      <div>
        <button onClick={this.nvl}>click</button>
        <p>{this.state.nom}</p>
        <p>{this.state.age}</p>
      </div>
    )
  }
}
export default App











