import React, { useState } from 'react'
import { useParams } from 'react-router'
import { useDispatch, useSelector } from 'react-redux'
import { useNavigate } from 'react-router'
import {ModifierAction} from "./Redux/actions/InitialAction"
export default function Modifier() {

  const {code}=useParams()
  const UpdatePay=useSelector(data=>data.py.find((py)=>py.code===parseInt(code)))
  const dispatch= useDispatch()
   const navigate=useNavigate()
    
    const [name,setName]=useState(UpdatePay.name)
    const [SurfaceArea,setSurfaceArea]=useState(UpdatePay.SurfaceArea)
    const [Population,setPopulation]=useState(UpdatePay.Population)
  
    
    
    const handlClick=()=>{
  dispatch(ModifierAction({
    code:code,
    name:name,
    SurfaceArea:SurfaceArea,
    Population:Population
  }))  
  navigate('/')
    }
  return (
    <div className='d-flex justify-content-center'>
        {/* le id code est {code} */}
        <form className='container card shadow-lg m-2 p-2 col-8 d-flex flex-column gap-3'>
       <div>
       <label>name</label>
        <input type="text" value={name} onChange={(e)=>setName(e.target.value)}/>
       </div>

       <div>
       <label>SurfaceArea</label>
        <input type="text" value={SurfaceArea} onChange={(e)=>setSurfaceArea(e.target.value)}/>
       </div>

       <div>
       <label>Population</label>
        <input type="text" value={Population} onChange={(e)=>setPopulation(e.target.value)}/>
       </div>
       <button onClick={handlClick}>valider</button>
      </form>
    </div>
  )
}
