import React from 'react'
import { useState } from 'react'
export default function App() {
  const velos = [
    {ref:'v1', marque:'ford', parcours:[] },
    {ref:'v2', marque:'ford', parcours:[] },
    {ref:'v3', marque:'ford', parcours:[] },
    {ref:'v4', marque:'ford', parcours:[] }
]

const [tab,setTab] = useState(velos)
const [autonomie,setautonomie]=useState()
const [kilometrage,setkilometrage]=useState()

function remplir(){
  tab.parcours.push({autonomie,kilometrage})
  setTab([...tab.parcours])

}
  return (
    <div>
      <input type="text"  placeholder='Enter autonomie' onChange={(e)=>setautonomie(e.target.value)} />
      <input type="text"   placeholder='Enter kilometrage'/>
      <button onClick={remplir}>ajouter</button>
      <div>
        <table>
          <tr>
            <th>autonomie</th>
            <th>kilometrage</th>
          </tr>
        </table>
      </div>
    </div>
  )
}
