import React, { useState } from 'react'

export default function App() {
  const[Loisirs,setILoisirs]=useState('')
  const[afficher,setAfficher]=useState()
  const lois=[]
 console.log(typeof(Loisirs))
  function afficherInfo(){

    lois.push(Loisirs)

    setAfficher(`mes loisirs sont `+lois.map((e)=>e))

  }

  return (
    <div className='text-center'>

      <h2 className='text-primary'>Inscriptipn</h2>
      <div>
        <b>Loisirs</b>
        <input type='checkbox' onClick={(e)=>setILoisirs(e.target.value)} value="Sport" name='Loisirs'/> <label>Sport</label> 
        <input type='checkbox' onClick={(e)=>setILoisirs(e.target.value)} value="Lecteur" name='Loisirs'/> <label>Lecteur</label> 
        <input type='checkbox' onClick={(e)=>setILoisirs(e.target.value)} value="Musique" name='Loisirs'/> <label>Musique</label> 
      </div>

      <div>
        <button onClick={afficherInfo} className='btn btn-primary my-3'>S'inscrire</button>
      </div>

      <h4>{afficher}</h4>

    </div>
  )
}
