import React from "react";
import { useParams } from "react-router-dom";
import { useSelector } from "react-redux";
const Q3 = () => {

  const { yearP } = useParams();
  const continents = useSelector((state) => state.continents.continents);
  return (
    <div className="container d-flex text-center">
      {yearP?
      continents.map((continent, index) => (
      continent.Pays.map(pay => (pay.indepYear === +yearP)?
      <div className="card" style={{ width: "18rem" }}>
      {/* <img
        class="card-img-top"
        src={continent.avatar}
        alt="Cardimagecap"
        style={{ height: "16rem" }}
      /> */}
      <div className="card-body">
        <h5 className="card-title">{pay.name}</h5>
        <p className="card-text">{pay.surfaceArea}</p>
        <p className="card-text">{pay.population}</p>
      </div>
    </div>:""
      
      )
      ))


      :continents.map((continent, index) => (
        <div className="card" style={{ width: "18rem" }}>
          <img
            className="card-img-top"
            src={continent.avatar}
            alt="Cardimagecap"
            style={{ height: "16rem" }}
          />
          <div className="card-body">
            <h5 className="card-title">{continent.name}</h5>
            <p className="card-text">{continent.surfaceArea}</p>
            <p className="card-text">{continent.population}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Q3;
