import {React, useState} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { filter } from './ContinentSlice';
export default function Q6(){
  const continents = useSelector(state => state.continents.continents)
  const FilterContinents = useSelector(state => state.continents.filterContinents);
  const dispatch = useDispatch();
  const [population, setPopulation]= useState()
  return(
    <div className='text-center m-5'>
      <select onChange={(e) => dispatch(filter({continent: e.target.value}))}>
        <option value="c">choisir une continents</option>
        {continents?continents.map((continent, index) => 
        <option key={index} value={continent.name}>{continent.name}</option>
        ):''}
      </select>
      <input type="text" onChange={(e) => setPopulation(e.target.value)}/> <button onClick={() => dispatch(filter({population: +population}))}>Search</button>
      {FilterContinents ?FilterContinents.map((pays,index) => 
      <div className="card" style={{ width: "18rem" }} key={index}>
        <div className="card-body">
            <h5 className="card-title">{pays.name}</h5>
            <p className="card-text">{pays.surfaceArea}</p>
            <p className="card-text">{pays.indepYear}</p>
        </div>
      </div>)
      :""} 
    </div>
  )
}
