import React from 'react';
import App from '../App';
import plantList from '../datas/plantList'
import imgSun from "../assets/sun.svg"
import imgWater from "../assets/water.svg"
export default function Pannier() {
  // var images=[]
  //   for(let i=0;i<plantList.length;i++){
  //     // images.push(<img src={imgWater} />)
     
  //   }  
  return (
    <div className='card shadow-lg m-3 p-3'>
      <div className='d-flex justify-content-center gap-3'>
         <select >
          {plantList.map((options,index)=>{
            return <option key={index+1} value={options.category}>{options.category}</option>
          })}
        </select><button className="btn btn-success">Search</button>
      </div>
      <div className='d-flex'>
      {plantList.map((imgs)=>{
            return <div className='container'>
             <div className="row">
              <div className='col-3'>
                      <div className="card">
                     <img src={imgs.cover} className="card-img-top" alt="image1"/>
                     <div className="card-body">
                       <h5 className="card-title">{imgs.name}</h5>
                       <p className="card-text"><img src={imgWater} alt="React Logo" /></p>
                       <button href="#" className="btn btn-success" >ajouter </button>
                     </div>
                      </div>
              </div>
              </div>
            </div> 
          })}
        </div>

      {/* <div className='container'>
