import React, { useState } from "react";

function Dashbord() {
    const [nomberMax,setNomberMax]=useState()
    const [score,setScore]=useState(0)
    const [essaie,setEssaie]=useState(3)
    const [resultatfinal,setresultatfinal]=useState()
    const [afficher,setafficher]=useState("")
    const [reponse,setReponse]=useState()
    const [Message,setMessage]=useState()
    const [MessageErr,setMessageErr]=useState()
    const [FinGame,setFinGame]=useState()
    const [stop,setStop]=useState(false)


    const calcMultip=()=>{
        let nomber1=Math.floor(Math.random()*nomberMax)
        let nomber2=Math.floor(Math.random()*nomberMax)
        let resultat=Number(nomber1)*Number(nomber2)

        if(nomberMax==null || nomberMax==0 ){
            setafficher('')
            setMessageErr("Entrez un nombre maximal, s'il vous plaît !")
        }else{
            setMessageErr('')
            setafficher(nomber1.toString() + ' X ' + nomber2.toString()); 
        }
        
        setresultatfinal(resultat)
       // Afficher le message d'erreur pendant 3 secondes
       setTimeout(() => {
        setMessageErr("");
      }, 3000);
      
    }


    const Verifier=()=>{
        if((nomberMax==null || nomberMax==0) ){
            setMessageErr("Entrez un nombre maximal, s'il vous plaît !")
        }else{
            setMessageErr('')
            if(reponse==null){
                setMessageErr("Entrez une réponse, s'il vous plaît !")
            }else{
                   if(resultatfinal===Number(reponse)){
            setMessage('Voilà !')
              setScore(score+1)
              calcMultip()
        }else{
            setMessage('Incorrect !')
            setEssaie(essaie===0?0:essaie-1)
        } 
            }
              
        }
       if(essaie===0){
        setStop(true)
        setFinGame(`veux fini le nombre de essaie et voila votre score : ${score}`)
       }
        // Afficher le message d'erreur pendant 3 secondes
        setTimeout(() => {
            setMessageErr("");
          }, 3000);
    }

  

    const handleEffacer = () => {
        window.location.reload(); // Recharge la page
        // setScore(0);
        // setNomberMax(0);
        // setresultatfinal('');
        // setafficher('');
        // setStop(false)
        // setReponse(0);
        // setMessage('');
        // setEssaie(3)
        // setFinGame('')
      };
      
      
    
  return (
    <frames>
    <div className="container card shadow-lg mt-4 p-3">
      <div className="d-flex justify-content-between">
        <h3 className="text-primary">
          Essayez-vous sur table de multiplication
        </h3>
        <div className="score fs-5 d-flex justify-content-center align-items-center  gap-4">

            <div>
                <span>nomber essaie: </span>
                <b className="text-danger">{essaie}</b>
            </div>
           <span className="text-success"> | </span>
            <div >
                 <span>Votre Score: </span>
                <b className="text-danger">{score}</b>
            </div>
         
        </div>
      </div>

      <hr />
      <div className="row justify-content-center">
        <h4 className="col-12 col-md-4 text-center text-success">
          Entrez le nombre maximal
        </h4>

        <div className="col-12 col-md-6">
          <input type="number"min="0"onChange={(e) => {setNomberMax(e.target.value)}} className="form-control"/>
        </div>

        <div className="col-12 col-md-2">
          <button
            onClick={calcMultip}
            type="submit"
            className="btn btn-success col-10"
          >
            Start
          </button>
        </div>
      </div>

      <div className="container mt-4">
        <div className=" container row col-12">
          <div className="d-flex col-6">
            <div className="col-12 col-md-6 fs-4 text-primary">
              nombre Calculer
            </div>
          </div>

          <div className="d-flex col-6">
            <input
              readOnly
              value={afficher}
              type="text"
              className="form-control col-6"
            />
          </div>
        </div>

        <div className=" container row col-12 mt-3">
          <div className="d-flex col-6">
            <div className="col-12 col-md-6 fs-4 text-primary">
              Votre reponse
            </div>
          </div>

          <div className="d-flex col-6">
          <input type="number" readOnly={stop} min='0' onChange={(e) => {setReponse(e.target.value)}}className="form-control col-6"/>
          </div>
        </div>

        <div className="container row col-12 mt-3">
          <div className="d-flex col-6">
            <div className="col-12 col-md-6 fs-4 text-primary">Message</div>
          </div>

          <div className="d-flex col-6">
            <input readOnly type="text"value={Message} className="form-control col-6" />
          </div>
        </div>
      </div>

      <div className="d-flex justify-content-center gap-3 mt-5 col-12">
        <button className="btn btn-danger col-5" onClick={handleEffacer} >Restart</button>
        <button className="btn btn-success col-5" onClick={Verifier}>Verifier</button>
      </div>

    </div>
    {MessageErr && (
       <div className="container col-sm-12 col-md-12 col-lg-12 text-center alert alert-danger mt-1">
          {MessageErr}
       </div>
     )}

{FinGame &&(
    <div className="container col-sm-12 col-md-12 col-lg-12 text-center alert alert-success mt-1">
    {FinGame}
  </div>
  )}
    </frames>
    
  );
}

export default Dashbord;
