import React, { useState } from 'react'
export default function App() {
     const list=[{
      name:"samsung",
      Pris:2000
     },
      {
      name:"xiaomi",
      Pris:4000
      }
    ];
    const[lists,setLists]=useState(list)
    const[nom,setNom]=useState()
    const[prix,setPrix]=useState()
    function remplir(){
        lists.push({name:nom,Pris:prix})
        setLists([...lists])
        setNom('')
        setPrix('')      
    }
  return (
    <div> 
        <input type="text" value={nom} placeholder='Enter Name' onChange={(e)=>setNom(e.target.value)}/>
        <input type="text" value={prix} placeholder='Enter price'onChange={(e)=>setPrix(e.target.value)}/>
        <button onClick={()=>remplir()} style={{backgroundColor:"aqua"}}>add</button>
        <br/>
       <table>
        <tr>
            <th>nom</th>
            <th>prix</th>
            <th>action</th>
        </tr>
        {
            lists?lists.map((v,indx)=>{
                return <tr key={indx+1}>
                    <td>{v.name}</td>
                    <td>{v.Pris}</td>
                </tr>
            }):""
        }
       </table>
    </div>
  )
}
