import React from 'react';
import Produits from './components/Produits';
import { BrowserRouter,Routes,Route } from 'react-router-dom';
import img1 from "./pictures/HP16D0195NF.jpg"
import img2 from "./pictures/HP14424U3EA.jpg"
import img3 from "./pictures/NXATHEF002.jpg"
import img4 from "./pictures/HUA6901443442959.jpg";
export default function App() {
  const products = [
    {
    id: 1,
    title: 'PC Portable Gamer HP VICTUS',
    price: '7490 DH',
    thumbnail : img1
    },
    {
    id: 2,
    title: 'PC Portable Gamer HP VICTUS',
    price: '2190 DH',
    thumbnail : img2
    },
    {
    id: 3,
    title: 'Pc Portable Chromebook Acer',
    price: '3640 DH',
    thumbnail: img3
    },
    {
    id: 4,
    title: 'PC Portable - HUAWEI',
    price: '1270 DH',
    thumbnail: img4
    }];
  return (

    <div>
      <Produits productsAp={products}/>
    </div>
  )
}












