//======exercice5:
import React, { useState } from 'react'

export default function App() {
const t=[{
id:1,marque:"Bosh",nomberVerre:10,boisson:[{nom:"cafe",prix:10,qte:0},{nom:"The",prix:8,qte:20},{nom:"Chocolat",prix:12,qte:3}]
},{
  id:2,marque:"Samsung",nomberVerre:20,boisson:[
    {nom:"cafe",prix:10,qte:50},
    {nom:"The",prix:8,qte:20},{nom:"Chocolat",prix:12,qte:40}]
    }
,{
  id:3,marque:"Toshiba",nomberVerre:5,boisson:[
    {nom:"cafe",prix:10,qte:50},
    {nom:"The",prix:8,qte:20},{nom:"Chocolat",prix:12,qte:40}]
    }
]
const[iid,setId]=useState()
const[afchT,setAfchT]=useState()

function Rechercher(){
  setAfchT(t.find((ft)=>ft.id==iid))
}

function handleReserve(indx){
const contrt=afchT.boisson.find((ft)=>ft.qte===indx)
contrt.qte=Number(contrt.qte)-1
   alert(contrt.qte=Number(contrt.qte)-1)
}

  return (
    <div>
      id: <input type='text' onChange={(e)=>setId(e.target.value)}/>
      <button onClick={Rechercher}>Rechercher</button><br/>
      Marque: <b>{afchT?afchT.marque:''}</b><br/>
      Nombre de verres: <b>{afchT?afchT.nomberVerre:''}</b><br/>
      <table>
        <tr>
          <th>Boisson</th>
          <th>Prix</th>
        </tr>
        {afchT?afchT.boisson.map((aft)=>{
          return <tr>
            <td>{aft.nom}</td>
            <td>{aft.prix}</td>
            <td>{aft.qte===0?<button>Desactivier</button>:<button onClick={()=>handleReserve(aft.qte)}>Servez-vous</button>}</td>
          </tr>
        }):''}
      </table>
    </div>
  )
}



