import React, { useState } from 'react';
import App from '../App';
import plantList from '../datas/plantList'
import imgSun from "../assets/sun.svg"
import imgWater from "../assets/water.svg"
export default function Pannier(props) {
 function afch(){
  props.bntAjt(1)
 }
  // var imagelstWtr=[
  //   <img src={imgWater}/>,
  //   <img src={imgWater}/>,
  //   <img src={imgWater}/>,
  //   <img src={imgWater}/>,
  //   <img src={imgWater}/>
  // ]
  // const [v,setV]=useState()
  



//  console.log(imagelstWtr)
var tb=[]
const water=(par)=>{
 for(let i=0;i<par;i++){
  tb.push(<img src={imgWater}/>)
  return tb
    }  
  }
 

  // let woter3=[
  //   <img src={imgWater}/>,
  //   <img src={imgWater}/>,
  //   <img src={imgWater}/>
  // ] 
  return (
    <div className='card shadow-lg p-3 col-9'>
      <div className='d-flex justify-content-center gap-3'>
         <select >
          {plantList.map((options,index)=>{
            return <option key={index+1} value={options.category}>{options.category}</option>
          })}
        </select><button className="btn btn-success">Search</button>
      </div>
      <div className='row mt-3'>
      {plantList.map((recp)=>{
            return  <div className='col-4'>
                      <div className="card">
                     <img src={recp.cover} className="card-img-top" id='imgCvr' alt="image1"/>
                     <div className="card-body">
                       <h5 className="card-title">{recp.name}</h5>
                       <p className="card-text">
                        {water(recp.water)}
                        {
                         tb.map((e)=>{
                          return e
                         })
                        }
                        {/* {
                        recp.water===3?woter3.map((w1)=>{
                          return w1
                        }): <img src={imgWater}/>
                        } */}
                        </p>
                        <div className='d-flex justify-content-between'>
                        <button href="#" className="btn btn-success" id='btnAjt' onClick={afch}>ajouter </button>
                        <span id='prix' className='btn border-success text-success'>{recp.price} $</span>
                        </div>
                     
                     </div>
                      </div>
              </div>
          })}
        </div> 
    </div>
  )
}
