import React from 'react'
import { useState } from 'react';
import Addlist from './components/Addlist';
export default function App() {
    const courses=[ 
        {id: "MarcheF",nom:"Marche femmes",categorie:"F", distance:10000
        }, 
       {id: "MarcheH",nom:"Marche hommes",categorie:"H", distance:10000
        }, 
       {id: "MarathonF",nom:"Marathon fommes",categorie:"F", distance:42000
        }, 
       {id: "MarathonH",nom:"Marathon hommes",categorie:"H", distance:42000
        } 
       ] ;

       const participants=[
       {id: "1", nom:"Med", idCourse: "MarcheH"}, 
       {id: "2",nom:"Rajae", idCourse: "MarcheF"} 
       ];
       const[affch,setAffch]=useState()
       const[btnavnt,setBtnavnt]=useState()
      function avent(){
        setBtnavnt(participants.find((e)=>e.id===participants[0].id))
      }
  return (
    <div className='d-flex flex-column'>
       <div>
       
        <span>Commetition d'athletisme </span>
       </div>
       <div>
        <span>Course Name : </span>
         <input type="text" onChange={(e)=>setAffch(e.target.value)}/>
         <button>{'|<'}</button>
         <button>{'<<'}</button>
         <button onClick={avent}>{'>>'}</button>
         <button>{'>|'}</button>
       </div>
       <div><span>Cathegorie :</span>
            <input type="radio" name='rad'/> Homme
            <input type="radio" name='rad'/> Fammme
        </div>
        <div>
            <span>Participants</span>
        </div>
        <table className='table-striped-columns border border-primary'>
            <tr className='border border-primary'>
                <th className='border border-primary'> ID participantsnt </th>
                <th className='border border-primary'> nom </th>
                <th className='border border-primary'> ID course </th>
                <th className='border border-primary'> Action </th>
            </tr>
            <tr>
                <td>{btnavnt?btnavnt.id:""}</td>
                <td>{btnavnt?btnavnt.id:""}</td>
                <td>{btnavnt?btnavnt.id:""}</td>
                <td>{btnavnt?btnavnt.id:""}</td>
            </tr>
           <Addlist prticp={participants} af={affch}/>
        </table>
    </div>
  )
}
