import React from 'react';
import './App.css';
export default function App() {
const [nom,setNom]=React.useState();
const [prenom,setPrenom]=React.useState();
const [reslt,setReslt]=React.useState();
 const afficher=()=>{
  setReslt('votre nom est: '+nom +"votre prenom est: "+ prenom)
}
  return (
    <div className='App'>
      <input type='text' className='nom' onChange={(e)=>setNom(e.target.value)}/>  <br/>
      <input type='text' className='prenom' onChange={(e)=>setPrenom(e.target.value)}/>  <br/>
      <button onClick={afficher}>click</button> <br/>
     <h2>{reslt}</h2>
    </div>
  )
}
