import React, { useState } from 'react'
export default function App() {
     const list=[{
      name:"samsung",
      Pris:2000
     },
      {
      name:"xiaomi",
      Pris:4000
      }
    ];
    const[lists,setLists]=useState(list)
    const[editStat,seteditStat]=useState(false)
    const[nom,setNom]=useState()
    const[prix,setPrix]=useState()

    function remplir(){
        lists.push({name:nom,Pris:prix})
        setLists([...lists])
        setNom('')
        setPrix('')      
    }

    function handlEdit(id){
      seteditStat(id)
    }
    function handleUpdate(){
        seteditStat(false)
    }
  return (
    <div> 
        <input type="text" value={nom} placeholder='Enter Name' onChange={(e)=>setNom(e.target.value)}/>
        <input type="text" value={prix} placeholder='Enter price'onChange={(e)=>setPrix(e.target.value)}/>
        <button onClick={()=>remplir()} className='btn btn-primary'>add</button>
        <br/>
       <table className='table-striped-columns border border-primary mt-4 col-8 text-center'>
        <tr className='border border-primary'>
            <th className='border border-primary'>nom</th>
            <th className='border border-primary'>prix</th>
            <th className='border border-primary'>action</th>
        </tr>
        {
            lists?lists.map((v,indx)=>{
                return editStat===indx+1? <Edit  handleUpdate={handleUpdate} rslt={v} indx={indx} lists={lists} setLists={setLists}/>: <tr key={indx+1} className='border border-primary'>
                    <td className='border border-primary'>{v.name}</td>
                    <td className='border border-primary'>{v.Pris}</td>
                    <td className='border border-primary'>
                      <button onClick={()=>handlEdit(indx+1)}>edit</button>
                      <button>delete</button>
                    </td>
                </tr>
            }):""
        }
       </table>
    </div>
  )
}

function Edit({rslt,indx,lists,setLists,handleUpdate}){
  function handlname(){
    const name=document.getElementById('name').value
    const updatData=lists.map((l,dx)=>dx+1==indx+1?{...l,name:name}:l)
    setLists(updatData)
  }
  function handlprice(){
    const price=document.getElementById('price').value
    const updatData=lists.map((l,dx)=>dx+1==indx+1?{...l,Pris:price}:l)
    setLists(updatData)
  }
  return(
    <tr className='border border-primary'>
      <td className='border border-primary'><input type='text' id='name' value={rslt.name} onChange={handlname}/></td>
      <td className='border border-primary'><input type='text' id='price' value={rslt.Pris} onChange={handlprice}/></td>
      <td className='border border-primary'><button type='button' onClick={()=>handleUpdate()}>Update</button></td>
    </tr>
  )
}


















