import React from 'react'
import { useDispatch,useSelector } from 'react-redux';
import { dark,light } from './components/store';
export default function Counter() {
  const bgclr=useSelector((data)=>data.couleurs.color)
  document.body.style=`background-color:${bgclr}`
  const dispatch=useDispatch()
  return (
    <div>
     
      <button onClick={()=>dispatch(increment())}>light</button>
      <button onClick={()=>dispatch(decrement())}>dark</button>
    
    </div>
  )
}
