import './App.css'
import React, { useState } from "react";
function App() {
const [time,setTime]= useState(0);

timer(()=>{
  setTimeout(()=>{
    setTime((time)=>time+1)
  },1000)
})

  return (
    <div> 
      <h1>afficher</h1>
     <h2>time est: {time}</h2>

    </div>
  )
}

export default App
