import {React, useState,useEffect} from 'react'
import { Link } from 'react-router-dom';
import courses from './ListeCourses';
import participants from './ListeParticipants';
export default function Courses(){
  const [index, setIndex] = useState(0);
  const [course, setCourse] = useState(courses[index]);
  useEffect(() => {
    setCourse(courses[index]);
  }, [index, course])
  const change = (state) => {
    if(state === 'first'){
      return setIndex(0);
    } else if (state === 'pre'){
      if(courses[index - 1]){ return setIndex(index - 1) }else setIndex(courses.length - 1)
    }else if(state === 'next'){
      if(courses[index + 1]){ return setIndex(index + 1) }else setIndex(0)

    }else if (state === 'last'){
      return setIndex(courses.length - 1)
    }
  }
  return (
  <>
      <div className="container">
    <h2>Compétition D'athletisme</h2>
    <div className="child">
      <label htmlFor="id">Course ID</label>
      <input type="text"  readOnly className='m-2' value={course.id?course.id: ""}/>
      <button onClick={()=>change("first")}>first</button>
      <button onClick={()=>change('pre')}>Pre</button>
      <button onClick={()=>change("next")}>next</button>
      <button onClick={()=>change("last")}>last</button>
    </div>
    <div className="child">
      <label htmlFor="name">Course Name</label>
      <input
        type="text"
        value={course.nom}
        id="courseName"
      />
    </div>
    <div className="child">
      <label htmlFor="cat">Cathégorie</label>
      <div>
        <input type="radio" name="sex" checked={course.categorie === 'H'?true:false}/> Homme
        <input type="radio" name="sex" checked={course.categorie === 'F'? true : false}/> Femme
      </div>
    </div>
    <div className="child">
      <label htmlFor="Distance">Distance</label>
      <input type="text" readOnly value={course.distance}/>
      <Link to={"/add/"+course.id}><button className='btn btn-primary m-2'>Nouveau participeau</button></Link>
    </div>
  </div>
  <div>
    <h2>Participants</h2>
    <table>
      <thead>
        <tr>
          <th>ID Participant</th>
          <th>Nom</th>
          <th>ID course</th>
        </tr>
      </thead>
      <tbody>
        {
          (course)?
          participants.filter((participant) => participant.idCourse === course.id).map((par, index) => 
          <tr key={index}>
            <td>{par.id}</td>
            <td>{par.nom}</td>
            <td>{par.idCourse}</td>
          </tr>)
          :""
        }
      </tbody>
    </table>
  </div>
  </>
  )
}
